        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external returns (bool success);
}

contracts/interfaces/ILendingProtocol.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ILendingProtocol
 * @notice Interface for Aave V3 Pool contract
 * @dev Abstraction of Aave V3 lending protocol functions used in the flash loan strategy
 */
interface ILendingProtocol {
    /**
     * @notice Executes a flash loan with a single asset
     * @param receiverAddress Address of the contract receiving the flash loan
     * @param asset Address of the asset being flash loaned
     * @param amount Amount of the asset to flash loan
     * @param params Arbitrary bytes-encoded parameters to pass to the receiver
     * @param referralCode Referral code for potential fee sharing (use 0 if none)
     */
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;

    /**
     * @notice Supplies an amount of underlying asset into the reserve
     * @param asset Address of the underlying asset to supply
     * @param amount Amount to be supplied
     * @param onBehalfOf Address that will receive the aTokens
     * @param referralCode Referral code for potential fee sharing (use 0 if none)
     */
    function supply(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external;

    /**
     * @notice Borrows an amount of underlying asset
     * @param asset Address of the underlying asset to borrow
     * @param amount Amount to be borrowed
     * @param interestRateMode Interest rate mode (1 = stable, 2 = variable)
     * @param referralCode Referral code for potential fee sharing (use 0 if none)
     * @param onBehalfOf Address that will incur the debt
     */
    function borrow(
        address asset,
        uint256 amount,
        uint256 interestRateMode,
        uint16 referralCode,
        address onBehalfOf
    ) external;

    /**
     * @notice Repays a borrowed amount
     * @param asset Address of the underlying asset to repay
     * @param amount Amount to repay (use type(uint256).max to repay all)
     * @param interestRateMode Interest rate mode (1 = stable, 2 = variable)
     * @param onBehalfOf Address whose debt will be repaid
     * @return amountRepaid The actual amount repaid
     */
    function repay(
        address asset,
        uint256 amount,
        uint256 interestRateMode,
        address onBehalfOf
    ) external returns (uint256 amountRepaid);

    /**
     * @notice Withdraws an amount of underlying asset from the reserve
     * @param asset Address of the underlying asset to withdraw
     * @param amount Amount to withdraw (use type(uint256).max to withdraw all)
     * @param to Address that will receive the underlying asset
     * @return amountWithdrawn The actual amount withdrawn
     */
    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external returns (uint256 amountWithdrawn);

    /**
     * @notice Sets whether a reserve should be used as collateral
     * @param asset Address of the underlying asset
     * @param useAsCollateral True to use as collateral, false otherwise
     */
    function setUserUseReserveAsCollateral(
        address asset,
        bool useAsCollateral
    ) external;

    /**
     * @notice Returns the user account data across all reserves
     * @param user Address of the user
     * @return totalCollateralBase Total collateral in base currency
     * @return totalDebtBase Total debt in base currency
     * @return availableBorrowsBase Available borrow capacity in base currency
     * @return currentLiquidationThreshold Current liquidation threshold
     * @return ltv Loan to value ratio
     * @return healthFactor Health factor (below 1.0 means liquidatable)
     */
    function getUserAccountData(address user)
        external
        view
        returns (
            uint256 totalCollateralBase,
            uint256 totalDebtBase,
            uint256 availableBorrowsBase,
            uint256 currentLiquidationThreshold,
            uint256 ltv,
            uint256 healthFactor
        );
}

contracts/mocks/MockAavePool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IFlashLoanSimpleReceiver.sol";

/**
 * @title MockAavePool
 * @notice Mock Aave V3 Pool contract for testing flash loan functionality
 * @dev Simplified implementation that simulates Aave behavior
 */
contract MockAavePool {
    using SafeERC20 for IERC20;

    // Flash loan fee: 0.05% = 5 basis points
    uint256 public constant FLASH_LOAN_FEE_BPS = 5;

    // Track user collateral and debt
    mapping(address => mapping(address => uint256)) public userCollateral;
    mapping(address => mapping(address => uint256)) public userDebt;
    mapping(address => mapping(address => bool)) public userCollateralEnabled;

    // Events
    event Supply(address indexed user, address indexed asset, uint256 amount);
    event Withdraw(address indexed user, address indexed asset, uint256 amount);
    event Borrow(address indexed user, address indexed asset, uint256 amount);
    event Repay(address indexed user, address indexed asset, uint256 amount);
    event FlashLoan(address indexed receiver, address indexed asset, uint256 amount, uint256 premium);

    /**
     * @notice Execute a flash loan
     */
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 /*referralCode*/
    ) external {
        uint256 premium = (amount * FLASH_LOAN_FEE_BPS) / 10000;

        // Transfer flash loaned amount to receiver
        IERC20(asset).safeTransfer(receiverAddress, amount);

        emit FlashLoan(receiverAddress, asset, amount, premium);

        // Call receiver's callback
        bool success = IFlashLoanSimpleReceiver(receiverAddress).executeOperation(
            asset,
            amount,
            premium,
            msg.sender,
            params
        );

        require(success, "Flash loan callback failed");

        // Take back the loan + premium
        IERC20(asset).safeTransferFrom(receiverAddress, address(this), amount + premium);
    }

    /**
     * @notice Supply (deposit) assets as collateral
     */
    function supply(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 /*referralCode*/
    ) external {
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);
        userCollateral[onBehalfOf][asset] += amount;

        emit Supply(onBehalfOf, asset, amount);
    }

    /**
     * @notice Enable or disable an asset as collateral
     */
    function setUserUseReserveAsCollateral(address asset, bool useAsCollateral) external {
        userCollateralEnabled[msg.sender][asset] = useAsCollateral;
    }

    /**
     * @notice Borrow assets against collateral
     */
    function borrow(
        address asset,
        uint256 amount,
        uint256 /*interestRateMode*/,
        uint16 /*referralCode*/,
        address onBehalfOf
    ) external {
        userDebt[onBehalfOf][asset] += amount;
        IERC20(asset).safeTransfer(msg.sender, amount);

        emit Borrow(onBehalfOf, asset, amount);
    }

    /**
     * @notice Repay borrowed assets
     */
    function repay(
        address asset,
        uint256 amount,
        uint256 /*interestRateMode*/,
        address onBehalfOf
    ) external returns (uint256) {
        uint256 currentDebt = userDebt[onBehalfOf][asset];
        uint256 amountToRepay = amount;

        if (amount == type(uint256).max) {
            amountToRepay = currentDebt;
        }

        if (amountToRepay > currentDebt) {
            amountToRepay = currentDebt;
        }

        userDebt[onBehalfOf][asset] -= amountToRepay;
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amountToRepay);

        emit Repay(onBehalfOf, asset, amountToRepay);

        return amountToRepay;
    }

    /**
     * @notice Withdraw supplied collateral
     */
    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external returns (uint256) {
        uint256 userBalance = userCollateral[msg.sender][asset];
        uint256 amountToWithdraw = amount;

        if (amount == type(uint256).max) {
            amountToWithdraw = userBalance;
        }

        require(amountToWithdraw <= userBalance, "Insufficient collateral");

        userCollateral[msg.sender][asset] -= amountToWithdraw;
        IERC20(asset).safeTransfer(to, amountToWithdraw);

        emit Withdraw(msg.sender, asset, amountToWithdraw);

        return amountToWithdraw;
    }

    /**
     * @notice Get user account data
     */
    function getUserAccountData(address user)
        external
        view
        returns (
            uint256 totalCollateralBase,
            uint256 totalDebtBase,
            uint256 availableBorrowsBase,
            uint256 currentLiquidationThreshold,
            uint256 ltv,
            uint256 healthFactor
        )
    {
        // Simplified mock - return reasonable values
        totalCollateralBase = 20000e8; // $20,000
        totalDebtBase = 15000e8; // $15,000
        availableBorrowsBase = 1000e8; // $1,000 available
        currentLiquidationThreshold = 8500; // 85%
        ltv = 8000; // 80%

        // Calculate health factor: (collateral * liquidationThreshold) / debt
        if (totalDebtBase > 0) {
            healthFactor = (totalCollateralBase * currentLiquidationThreshold) / totalDebtBase / 100;
        } else {
            healthFactor = type(uint256).max;
        }

        return (
            totalCollateralBase,
            totalDebtBase,
            availableBorrowsBase,
            currentLiquidationThreshold,
            ltv,
            healthFactor
        );
    }

    /**
     * @notice Fund the pool with tokens for testing
     */
    function fundPool(address asset, uint256 amount) external {
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);
    }
}

contracts/mocks/MockArbitrageExecutor.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IArbitrageExecutor.sol";

/**
 * @title MockArbitrageExecutor
 * @notice Mock arbitrage executor for testing the flash loan strategy
 * @dev Simulates arbitrage by simply returning the required amount (plus optional profit)
 */
contract MockArbitrageExecutor is IArbitrageExecutor {
    using SafeERC20 for IERC20;

    bool public shouldSucceed = true;
    bool public shouldReturnEnough = true;
    uint256 public profitAmount = 0;

    event ArbitrageExecuted(address token, uint256 amount, uint256 returned);

    /**
     * @notice Execute mock arbitrage strategy
     * @dev Simulates arbitrage by returning tokens with optional profit
     */
    function executeArbitrage(
        address token,
        uint256 amount,
        uint256 requiredReturn
    ) external override returns (bool) {
        if (!shouldSucceed) {
            return false;
        }

        // Simulate arbitrage "work" - in real scenario, this would be DEX trades, etc.
        // For testing, we just prepare the return amount

        uint256 returnAmount;
        if (shouldReturnEnough) {
            returnAmount = requiredReturn + profitAmount;
        } else {
            returnAmount = requiredReturn / 2; // Return insufficient amount
        }

        // Transfer tokens back to the strategy contract
        IERC20(token).safeTransfer(msg.sender, returnAmount);

        emit ArbitrageExecuted(token, amount, returnAmount);

        return true;
    }

    /**
     * @notice Set whether the arbitrage should succeed
     */
    function setShouldSucceed(bool _shouldSucceed) external {
        shouldSucceed = _shouldSucceed;
    }

    /**
     * @notice Set whether the arbitrage should return enough tokens
     */
    function setShouldReturnEnough(bool _shouldReturnEnough) external {
        shouldReturnEnough = _shouldReturnEnough;
    }

    /**
     * @notice Set the profit amount to return
     */
    function setProfitAmount(uint256 _profitAmount) external {
        profitAmount = _profitAmount;
    }

    /**
     * @notice Receive tokens (for testing)
     */
    receive() external payable {}
}

